import styled from 'styled-components'

function Single({backgroundImg ,title ,location}) {
  return (
    <Cover>
    <Wrap bgimage ={backgroundImg}>

    </Wrap>
    <h3>{title}</h3>
    <span>{location}</span>
    </Cover>
  )
}

export default Single
const Cover = styled.div`


`

